    ORDER_LIST_SUCCESS,
    ORDER_DELETE_REQUEST,
    ORDER_DELETE_FAIL,
    ORDER_DELETE_SUCCESS,
    ORDER_DELIVER_REQUEST,
    ORDER_DELIVER_SUCCESS,
    ORDER_DELIVER_FAIL,} from '../constants/orderConstants';

export const createOrder = (order) => async (dispatch, getState) => {
    dispatch({type: ORDER_CREATE_REQUEST, payload: order});
    try{
        /*Importing userInfo from userSignIn*/
        const {userSignin: { userInfo }} = getState();
        /*AJAX Request*/
        const { data } = await Axios.post('api/orders', order, {
            headers: {
                Authorization: `Bearer ${userInfo.token}`,
            },
        });
        dispatch({ type: ORDER_CREATE_SUCCESS, payload: data.order});
        dispatch({type: CART_EMPTY});
        localStorage.removeItem('cartItems');

    } catch (error) {
        dispatch({
            type: ORDER_CREATE_FAIL,
            payload:
                error.response && error.response.data.message
                ? error.response.data.message
                : error.message,
    });
  }
};

/*Defining detailsOrder from the orderScreen --> useEffect*/

export const detailsOrder = (orderId) => async(dispatch, getState) => {
    dispatch({ type: ORDER_DETAILS_REQUEST, payload: orderId });
    
    /*Geeting functional of getState and importing userInfo data*/
    const {userSignin : {userInfo}} = getState();
    
    try{ /*AJAX request*/
        const { data } = await Axios.get(`/api/orders/${orderId}`, {
            headers: { Authorization: `Bearer ${userInfo.token}`}
        });                                    /*userInfo is obtained from getState*/
        dispatch({ type: ORDER_DETAILS_SUCCESS, payload: data});
    } catch (error) {
        const message = error.message && error.response.data.message
        ? error.response.data.message
        : error.message;
        dispatch({ type: ORDER_DETAILS_FAIL, payload: message});
    }
};


