/*For viewing and editing/deleting the orders under ADMIN*/
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { deleteOrder, listOrders } from "../actions/orderActions";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";

export default function OrderListScreen(props) {
    /*getting OrderList object from OrderReducer*/
    const orderList = useSelector(state => state.orderList);
    const {loading, error, orders} = orderList;


    /*getting orderDelete from redux store i.e. from store.js*/
    const orderDelete = useSelector(state => state.orderDelete);
    const {loading: loadingDelete, error: errorDelete, success: successDelete} = orderDelete;


    const dispatch = useDispatch();
    useEffect (() => {
        dispatch(listOrders()); /*Need to define this fx in orderActions.js in actions folder.*/
    }, [dispatch]);

    const deleteHandler = (orders) => {
        if(window.confirm('Are you sure to delete?')) {
            dispatch(deleteOrder(order._id));
        }
    };

    return (
        <div className = "container">
            <h1 /*We need to check 'loading' first*/>Orders by Customers</h1>
            {loading ? ( <LoadingBox></LoadingBox> 
             ): ( error ? <MessageBox variant = "danger">{error}</MessageBox>
             : (
                 <table className="table">
                     <thead>
                     <tr>
                        <th>ORDER ID NO</th>
                        <th>USER_NAME</th>
                        <th>DATE</th>
                        <th>TOTAL</th>
                        <th>PAID/NOT PAID</th>
                        <th>DELIVERED/HOLDING</th>
                        <th>ACTIONS</th>
                     </tr>
                     </thead>
                     <tbody /*Now looking over orders and using map fx to convert each orderobject into JSX*/>
                         {orders.map((orders) => (
                             <tr key={orders._id}>
                             <td>{orders._id}</td>
                             <td>{orders.user.name}</td>
                             <td>{orders.createdAt.substring(0, 10)}</td>
                             <td>Rs.{orders.totalPrice.toFixed(2)}/-</td>
                             <td /*isPaid is conditional. If it is true (
                                 ?) then show order.paidAt for showing 
                                 only date part and not time. Substring is used to capture only 
                                 date and not the time of the payment. If this condition is
                                 false, then it will show not payment not paid 
                                 */>
                                     {orders.isPaid ? orders.paidAt.substring(0, 10) : 'Not Paid'}         
                            </td>
                             
                             <td>{orders.isDelivered ? orders.deliveredAt.substring(0, 10) : 'Not Delivered'}</td>
                             <td>
                                 <button type="button" className="btn btn-primary"
                                 onClick={() => {
                                     props.history.push(`/order/${orders._id}`);
                                 }}>Order Details</button>

                                 <button 
                                 className="small"
                                 type = "button"
                                 onClick={() => deleteHandler(orders)}>
                                     Delete
                                 </button>
                             </td>
                             
                         </tr>
                         )
                            
                         )}
                     </tbody>
                 </table>

             )
             )
            }

        </div>
    )
        
    
}
