import  Axios  from "axios";
import { USER_REGISTER_FAIL, USER_REGISTER_REQUEST, USER_REGISTER_SUCCESS, USER_SIGIN_FAIL, USER_SIGIN_REQUEST, USER_SIGIN_SIGNOUT, 
    USER_SIGIN_SUCCESS } from "../constants/userConstants"

export const signin = (email, password) => async(dispatch) => {
    dispatch ({ type: USER_SIGIN_REQUEST, payload: { email, password}});
    try {
        /*AJAX  post request for signin*/
        const { data } = await Axios.post('/api/users/signin', { email, password });
        dispatch({ type: USER_SIGIN_SUCCESS, payload: data });
        localStorage.setItem('userInfo', JSON.stringify(data));
    } catch (error) {
        dispatch({
            type: USER_SIGIN_FAIL,
            payload:
            error.response && error.response.data.message
            ? error.response.data.message
            : error.message,

        });
    }    
};

export const signout = () => (dispatch) =>{
    localStorage.removeItem('userInfo');
    localStorage.removeItem('cartItems');
    localStorage.removeItem('shippingAddress');
    dispatch({type: USER_SIGIN_SIGNOUT});
};


/*New User Registration*/
export const register = (name, email, password) => async(dispatch) => {
    dispatch ({ type: USER_REGISTER_REQUEST, payload: { name, email, password}});
    try {
        /*AJAX  post request for signin*/
        const { data } = await Axios.post('/api/users/register', { name, email, password });
        dispatch({ type: USER_REGISTER_SUCCESS, payload: data });
        /*The below code is to update redux that a new user has registered*/
        dispatch({ type: USER_SIGIN_SUCCESS, payload: data });
        localStorage.setItem('userInfo', JSON.stringify(data));
    } catch (error) {
        dispatch({
            type: USER_REGISTER_FAIL,
            payload:
            error.response && error.response.data.message
            ? error.response.data.message
            : error.message,

        });
    }    
};
