import React, { useState } from 'react';
import CheckoutSteps from '../components/CheckoutSteps';

export default function ShippingAddressScreen(props) {
    useState [fullName, setFullName] = useState(' ');
    useState [address, setAddress] = useState(' ');
    useState [city, setCity] = useState(' ');
    useState [postalCode, setPostalCode] = useState(' ');
    useState [country, setCountry] = useState(' ');
    const submitHandler = (e) => {
        e.preventDefault();

    };
    return (
        <div>
            <CheckoutSteps step1 step2></CheckoutSteps>
            <form className="form" onSubmit={submitHandler}>
                <div>
                    <h2>Shipping Address</h2>
                </div>

                <div>
                    <label htmlFor="fullName">Full Name</label>
                    <input type="text" 
                    id="fullName" 
                    placeholder="Enter your full name" 
                    value={fullName} 
                    onChange={(e) => setFullName(e.target.value)} 
                    required>

                    </input>
                </div>

                <div>
                    <label htmlFor="fullName">Address</label>
                    <input type="text" 
                    id="fullName" 
                    placeholder="Enter your full name" 
                    value={fullName} 
                    onChange={(e) => setFullName(e.target.value)} 
                    required>

                    </input>
                </div>

                <div>
                    <label htmlFor="fullName">City</label>
                    <input type="text" 
                    id="fullName" 
                    placeholder="Enter your full name" 
                    value={fullName} 
                    onChange={(e) => setFullName(e.target.value)} 
                    required>

                    </input>
                </div>

                <div>
                    <label htmlFor="country">Country</label>
                    <input type="text" 
                    id="country" 
                    placeholder="Your Country?" 
                    value={country} 
                    onChange={(e) => setcountry(e.target.value)} 
                    required>

                    </input>
                </div>

                <div>
                    <label htmlFor="fullName">Full Name</label>
                    <input type="text" 
                    id="fullName" 
                    placeholder="Enter your full name" 
                    value={fullName} 
                    onChange={(e) => setFullName(e.target.value)} 
                    required>

                    </input>
                </div>

                <div>
                    <label htmlFor="fullName">Full Name</label>
                    <input type="text" 
                    id="fullName" 
                    placeholder="Enter your full name" 
                    value={fullName} 
                    onChange={(e) => setFullName(e.target.value)} 
                    required>

                    </input>
                </div>
            </form>
        </div>
    )
}
