import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { createReview, detailsProduct } from '../actions/productActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import Rating from '../components/Rating';
import { PRODUCT_REVIEW_RESET } from '../constants/productConstants';

export default function ProductScreen(props) {
  const dispatch = useDispatch();
  const productId = props.match.params.id;
  const [qty, setQty] = useState(1);
  const productDetails = useSelector((state) => state.productDetails);
  const { loading, error, product } = productDetails;


  /*Importing userInfo*/
  const userSignin = useSelector((state) => state.userSignin);
  const {userInfo} = userSignin;

 /*Importing product review from Store.js*/
 const productReviewCreate = useSelector((state) => state.productReviewCreate);
 const {loading: loadingReview, error: errorReview, success: successReview} = productReviewCreate;
  

/*Hooks for Rating and comments*/
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');

  useEffect(() => {
    if(successReview) {
      window.alert('Thanks for your review!');
      setComment('');
      setRating('');
      dispatch({ type: PRODUCT_REVIEW_RESET});
    }
    dispatch(detailsProduct(productId));
  }, [dispatch, productId, successReview]);
  const addToCartHandler = () => {
    props.history.push(`/cart/${productId}?qty=${qty}`);
  };

  const submitHandler = (e) => {
    e.preventDefault();
    if(comment && rating) {
      dispatch(
        createReview(productId, {rating, comment, name: userInfo.name})
        /*Implementing createComment fx in ProductActions.js*/
      );
    } else {
      alert('Please give rating and enter comment');
    }
  }


  return (
    <div>
      {loading ? (
        <LoadingBox></LoadingBox>
      ) : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
      ) : (
        <div>
          <Link to="/">Back to result</Link>
          <div className="row top">
            <div className="col-2">
              <img
                className="large"
                src={product.image}
                alt={product.name}
              ></img>
            </div>
            <div className="col-1">
              <ul>
                <li>
                  <h1>{product.name}</h1>
                </li>
                <li>
                  <Rating
                    rating={product.rating}
                    numReviews={product.numReviews}
                  ></Rating>
                </li>
                <li>Pirce : ${product.price}</li>
                <li>
                  Description:
                  <p>{product.description}</p>
                </li>
              </ul>
            </div>
            <div className="col-1">
              <div className="card card-body">
                <ul>
                  <li /*To Link to the Seller here and also to view the Reviews*/>
                    Seller{' '}
                    <h2>
                      <Link to={`/seller/${product.seller._id}`}>
                        {product.seller.seller.name}
                      </Link>
                    </h2>
                    
                    <Rating rating = {product.seller.seller.rating}
                    numReviews = {product.seller.seller.numReviews}>
                      
                    </Rating>
                  </li>

                  <li>
                    <div className="row">
                      <div>Price</div>
                      <div className="price">${product.price}</div>
                    </div>
                  </li>
                  <li>
                    <div className="row">
                      <div>Status</div>
                      <div>
                        {product.countInStock > 0 ? (
                          <span className="success">In Stock</span>
                        ) : (
                          <span className="danger">Unavailable</span>
                        )}
                      </div>
                    </div>
                  </li>
                  {product.countInStock > 0 && (
                    <>
                      <li>
                        <div className="row">
                          <div>Qty</div>
                          <div>
                            <select
                              value={qty}
                              onChange={(e) => setQty(e.target.value)}
                            >
                              {[...Array(product.countInStock).keys()].map(
                                (x) => (
                                  <option key={x + 1} value={x + 1}>
                                    {x + 1}
                                  </option>
                                )
                              )}
                            </select>
                          </div>
                        </div>
                      </li>
                      <li>
                        <button
                          onClick={addToCartHandler}
                          className="primary block"
                        >
                          Add to Cart
                        </button>
                      </li>
                    </>
                  )}
                </ul>
              </div>
            </div>
          </div>


          <div>
            <h2 id = "reviews">Reviews</h2>
            {product.reviews.length === 0 && (<MessageBox>No Reviews</MessageBox>)}
            <ul /*map fx used to convert each reviews objects to JSX objects*/>
              {product.reviews.map((review) => {
                <li key = {review._id}>
                  <strong /*Showing name who reviewed*/>{review.name}</strong>
                  <Rating rating={review.rating} caption = " "></Rating>
                  <p /*The caption is kept blank to put the p tag data and show it*/>
                    {review.createdAt} 
                  </p>
                    
                  <p /*For viewing comment*/>
                       {review.comment}
                  </p>
                </li>
              })}

                <li /*A form section to write the reviews and user need to
                login for passing a review*/>
                    {userInfo ? (
                      <form className='form'
                      onSubmit={submitHandler}>
                        <div>
                          <h2>Write a review for the product</h2>
                        </div>

                        <div>
                          <label htmlFor='rating'>Rating</label>
                          <select id="rating" value={rating} onChange={(e) => setRating(e.target.value)}>
                            <option value = "">Select Rating</option>
                            <option value = "1">1 - Poor</option>
                            <option value = "2">2 - Fair</option>
                            <option value = "3">3 - Good</option>
                            <option value = "4">4 - Very Good</option>
                            <option value = "5">5 - Excellent</option>
                          </select>
                        </div>

                        <div /*for text area*/>
                          <label htmlFor='comment'>Comment</label>
                          <textarea id="comment"
                          value = {comment}
                          onChange={(e) => setComment(e.target.value)}></textarea>
                        </div>

                        <div /*for submit button*/>
                          <label/>
                          <button className='primary' type='submit'>Submit</button>
                        </div>

                        <div>
                          {loadingReview && <LoadingBox></LoadingBox>}
                          {errorReview && <MessageBox variant="danger">{errorReview}</MessageBox>}
                        </div>
                      </form>
                    ) : (
                        <MessageBox /*If a user is not logged in*/
                        variant="danger">Sorry! Login required Pls  <Link to="/signin">Login here</Link></MessageBox>
                    )}
                </li>
            </ul>
        </div>
      )
    </div>
  
      )}
  </div>
  )
}
